mapper.select:
	
	select * from place 
	
	where 1=1 
	
	
         and channel=#{channel}
    
    	
         and type=#{type}
    
    	
         and pl_id=#{pl_id}
    
    	
         and flag=#{flag}
    
    	
         and display=#{display}

mapper.update:

update place
set 
type=#{type},display=#{display},number=#{number},flag=#{flag},remark=#{remark}
where pl_id =#{pl_id} and channel=#{channel}
