mapper.select:
select * from dt_brand where 1=1


	and brand_type=#{brand_type}


	and name like '%'+#{name}+'%'

mapper.update:
update dt_brand set
name=#{name}, logo=#{logo}, brand_type=#{brand_type}, introduction=#{introduction}
where id=#{id}

mapper.select:
select count(*) from dt_brand where 1=1

	
	
		and brand_type=#{brand_type}
	
	
		and name like '%'+#{name}+'%'
